<?phpfunction wt_last_posts_load_widget2(){    register_widget('wt_last_posts_widget2');}add_action('widgets_init', 'wt_last_posts_load_widget2');class wt_last_posts_widget2 extends WP_Widget{    function __construct()    {        parent::__construct(            'wt_last_posts_widget2',            'آخرین مقالات با عکس',            array('description' => '10 تا از آخرین مقالات همراه عکس.',)        );    }    public function widget($args, $instance)    {        $title = apply_filters('widget_title', $instance['title']);        $last_posts_args = array(            'post_type' => 'post',            'post_status' => 'publish',            'orderby' => 'date',            'order' => 'DESC',            'posts_per_page' => 10        );        $the_query = new WP_Query($last_posts_args);        ?>        <div class="post-sidebar-item">            <div class="post-sidebar-head">                <h5><?php echo $title ?></h5>            </div>            <div class="wp-widget-group__inner-blocks">                <ul class="wp-block-latest-posts__list wp-block-latest-posts">                    <?php                    if ($the_query->have_posts()) {                        while ($the_query->have_posts()) : $the_query->the_post(); ?>                            <li>                                <a href="<?php the_permalink(); ?>">                                    <div class="thumb">                                        <img width="46" height="39" src="<?php the_post_thumbnail_url('very-small'); ?>"                                             alt="<?php the_title(); ?>">                                    </div>                                    <h2 class="title"><?php the_title(); ?></h2>                                </a>                            </li>                        <?php endwhile;                        wp_reset_postdata();                    } else {                        echo '<li>مقاله ای وجود ندارد.</li>';                    }                    ?>                </ul>            </div>        </div>        <?php    }    public function form($instance)    {        if (isset($instance['title'])) {            $title = $instance['title'];        } else {            $title = 'جدیدترین مقالات';        }        ?>        <p>            <label for="<?php echo $this->get_field_id('title'); ?>"><?php _e('Title:'); ?></label>            <input class="widefat" id="<?php echo $this->get_field_id('title'); ?>"                   name="<?php echo $this->get_field_name('title'); ?>" type="text"                   value="<?php echo esc_attr($title); ?>"/>        </p>        <?php    }    public function update($new_instance, $old_instance)    {        $instance = array();        $instance['title'] = (!empty($new_instance['title'])) ? strip_tags($new_instance['title']) : '';        return $instance;    }}